import React from 'react';
import ReactDOM from 'react-dom';
import io from "socket.io-client";

import Leaflet from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';

import './index.css';

delete Leaflet.Icon.Default.prototype._getIconUrl;

Leaflet.Icon.Default.mergeOptions({
  shadowSize: null,
  iconSize: new L.Point(40, 40),
  className: 'airplane-icon',
});

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      nickname: '',
      messages: [],
      flights: [],
      positions: [],
    }
  }

  componentDidMount () {
    this.socket = io.connect('wss://tarea-3-websocket.2021-1.tallerdeintegracion.cl', {
      path: '/flights/',
      transports: ['websocket'],
    });

    this.socket.on('CHAT', message => {
      this.setState({ messages: [...this.state.messages, message]})
    });

    this.socket.on('FLIGHTS', flights => {
      this.setState({ flights: [...this.state.flights, flights]})
    });

    this.socket.on('POSITION', position => {
      this.setState({ positions: [...this.state.positions, position]})
    });
  }

  handleNickname = event => {
    const nickname = event.target.value
    if (event.keyCode === 13 && nickname) {
      this.setState({ nickname: nickname})
      event.target.value = ''
    }
  }

  handleSubmit = event => {
    if (this.state.nickname) {
      const body = event.target.value
      if (event.keyCode === 13 && body) {
        const message = {
          name: this.state.nickname,
          message: body
        }
        this.socket.emit('CHAT', message)
        event.target.value = ''
      }
    }

  }

  render() {
    const messages = this.state.messages.map((message, index) => {
      return <li key={index}>
        <b>{message.name}: {message.message}</b>
      </li>
    });

    return(
      <div>
        <h1>Informaci√≥n Vuelos</h1>

        <div className="container">
          <div className="map">
            <MapContainer className="map" center={[-36, -70]} zoom={5} scrollWheelZoom={false}>
              <TileLayer
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
            </MapContainer>
          </div>

          <div className="chat">
            <h2>Chat</h2>

            <div className="nickname">
              <input
                type="text"
                placeholder='Ingresa tu nombre'
                onKeyUp={this.handleSubmit}>
              </input>
            </div>

            <div className="messages">
              <input
                type="text"
                placeholder='Ingresa tu mensaje'
                onKeyUp={this.handleSubmit}>
              </input>
              {messages}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

ReactDOM.render(<App/>, document.getElementById('root'));
