import React from 'react';
import ReactDOM from 'react-dom';
import io from "socket.io-client";

import './index.css';

import Leaflet from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';

delete Leaflet.Icon.Default.prototype._getIconUrl;

Leaflet.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      nickname: '',
      messages: [],
      flights: [],
      positions: {},
      paths: {},
    }
  }

  componentDidMount () {
    this.socket = io.connect('wss://tarea-3-websocket.2021-1.tallerdeintegracion.cl', {
      path: '/flights/',
      transports: ['websocket'],
    });

    this.socket.on('CHAT', message => {
      this.setState({ messages: [...this.state.messages, message]})
    });

    this.socket.on('FLIGHTS', flights => {
      this.setState({ flights: flights})
    });

    this.socket.on('POSITION', position => {
      this.setState(prevState => ({
        positions: {
            ...prevState.positions,
            [position.code]: position.position,
        },
      }));
      this.socket.emit('FLIGHTS', {})
      this.state.flights.map((flight, index) => {
        console.log(flight['airline'])
      })
    });
  }

  handleNickname = event => {
    const nickname = event.target.value
    if (event.keyCode === 13 && nickname) {
      this.setState({ nickname: nickname})
      event.target.value = ''
    }
  }

  handleSubmit = event => {
    if (this.state.nickname) {
      const body = event.target.value
      if (event.keyCode === 13 && body) {
        const message = {
          name: this.state.nickname,
          message: body
        }
        this.socket.emit('CHAT', message)
        event.target.value = ''
      }
    }

  }

  render() {
    const messages = this.state.messages.map((message, index) => {
      return <li key={index}>
        <b>{message.name}: {message.message}</b>
      </li>
    });

    const flights = this.state.flights.map((flight, index) => {
      return <li key={index}>
        <div className="flight-info">
          <p className="flight-info-line"><b>Vuelo {flight['code']}</b></p>
          <p className="flight-info-line">Aerolínea {flight['airline']}</p>
          <p className="flight-info-line">Origen {flight['origin']}</p>
          <p className="flight-info-line">Destino {flight['destination']}</p>
          <p className="flight-info-line">Avión {flight['plane']}</p>
          <p className="flight-info-line">Asientos {flight['seats']}</p>
        </div>     
      </li>
    });

    return(
      <div>
        <div className="title-container">
          <h1>Información Vuelos</h1>
        </div>

        <div className="base-container">
          <div className="map-container">
            <div>
              <MapContainer className="map" center={[-36, -70]} zoom={5} scrollWheelZoom={false}>
                <TileLayer
                  attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                {Object.keys(this.state.positions).map((key, index) => (
                  <Marker key={key} position={this.state.positions[key]}>
                    <Popup>{key}</Popup>
                  </Marker>
                ))}
              </MapContainer>
            </div>
          </div>

          <div className="messages-container">
            {flights}
          </div>

          <div className="chat-container">
            <h2>Centro de Control</h2>

            <div className="nickname-container">
              <input
                type="text"
                placeholder='Ingresa tu nombre'
                onKeyUp={this.handleNickname}>
              </input>
            </div>

            <div className="messages-container">
              <div className="messages-input-container">
                <input
                  type="text"
                  placeholder='Ingresa tu mensaje'
                  onKeyUp={this.handleSubmit}>
                </input>
              </div>
              {messages}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

ReactDOM.render(<App/>, document.getElementById('root'));
